import React from 'react';
import PropTypes from 'prop-types';

export default class Board extends React.Component{

        constructor(props){
            super(props)
            this.state = {
            boardData: this.initBoardData(props.dimension,props.mines),
            gameStatus:false,
            mineCount:this.props.mines
            }
        }

    createEmptyBoard(dimension) {
        let data = []
        for(let i=0;i<dimension.length;i++){
            data.push([])
            for(let j=0;j<dimension.length;j++){
                data[i][j] = {
                    x: i,
                    y: j,
                    isMine: false,
                    neighbour: 0,
                    isRevealed: false,
                    isEmpty: false,
                    isFlagged: false,
                    };

                }
            }
            return data;


    }
    initBoardData(){
        this.createEmptyBoard(this.state.boardData.dimension)
        // this.plantMines()
    }
    render(){
        return(
            <div className="board">
      <div className="game-info">
        <span className="info">
          mines: {this.state.mineCount}
        </span>
        <br />
        <span className="info">
          {this.state.gameStatus}
        </span>
      </div>
      { this.renderBoard(this.state.boardData)}
    </div>
    );
    }

}
Board.propTypes = {
    dimension: PropTypes.number,
    mines: PropTypes.number,
  }
